//imports
import React from "react";
import { useState, useEffect } from "react";

//home page
export default function Home() {
  const [clicked, setClicked] = useState(false);
  const [results, setResults] = useState();

  useEffect(() => {
    //   if the list is empty
    if (clicked) {
    // get it by calling the api endpoint
    fetch("/api")
      .then((res) => res.json())
      .then((restList) => {
        // then set it in state
        setResults(restList);
      });
    setClicked(false)
  }}, [clicked]);



//   let answer = JSON.stringify(results)

let chatArray = [];
  results &&
    results.forEach((object) => {
      console.log(object);
      chatArray.push(object.text);
    });

  return (
    <div>
      <h1>Mutha fackin Home Page</h1>

      {/* button to signal fetch */}
      <button
        onClick={() => {
          setClicked(true);
        }}
      >
        CLICK ME!
      </button>

    {/*printing unformatted results to page  */}
      {/* <div>{answer}</div> */}

      {chatArray.map((chat,index) => {
          return (<div key={index}>{chat}</div>)
        })}
    </div>
  );
}
